import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import {Dimensions, StyleSheet, Text, View, ScrollView, Image} from 'react-native';
import Header from './components/Header';

export default function App() {
  const [followers, setFollowers] = useState([]);

  useEffect(() => {
    fetch("https://api.github.com/users/Unn4m3DD/followers")
          .then(res=>res.json())
          .then(data=>setFollowers(data))
          .catch(e => console.log(e))

    followers.forEach((follower) => {
      follower.bio = fetch(follower.url).then(data => data.json())
    })
  });
  return (
    <View>
      <Header headerTitle='Github Social Club'/>
      <ScrollView style={styles.container, {height: Dimensions.get('window').height * 0.9}}>{
        followers.map((follower) => {
          console.log(follower)
          return(
            <View key={follower.id} style={{flexDirection: 'row', backgroundColor: 'ghostwhite'}}>
              <Image source={{uri: follower.avatar_url}} style={styles.image}/>
              <View>
              <Text style={styles.text}>{follower.login}</Text>
              <Text style={styles.text}>{follower.bio}</Text>
              </View>
            </View>
          )
        })
      }</ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'black',
  },
  text: {
    textAlign: 'center',
  },
  image: {
    width: 100,
    height: 100,
  }
});
