import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import {Dimensions, StyleSheet, Text, View, ScrollView, Image} from 'react-native';
import {createAppContainer} from 'react-navigation';
import {createStackNavigator} from 'react-navigation-stack';
import Header from './components/Header';
const {height} = Dimensions.get('window')

function followers() {
  const [followers, setFollowers] = useState([]);
  const user = navigator.params
  console.log(user)
  useEffect(() => {
    loadData()
  },[]);

  return (
    <View style={{flex: 1}}>
      <Header headerTitle='Github Social Club'/>
      <ScrollView style={styles.container}>{
        followers.map((follower) => {
          return(
            <View key={follower.id} style={{flexDirection: 'row', backgroundColor: 'ghostwhite'}}>
              <Image source={{uri: follower.avatar_url}} style={styles.image}/>
              <View style={{flex : 1, flexDirection : 'column', justifyContent : 'flex-start', alignItems:'center'}}>
              <Text style={styles.username}>{follower.login}</Text>
              <Text style={styles.bio}>{follower.bio}</Text>
              </View>
            </View>
          )
        })
      }</ScrollView>
    </View>
  );

  async function loadData() {
    const data = await fetch("https://api.github.com/users/" + user + "/followers")
          .then(res=>res.json())
    for(let follower of data) {
      follower.bio = await fetch(follower.url).then(data => data.json()).then(data=>data.bio)
    }
    setFollowers(data)
  }
}

const AppNavigator = createStackNavigator(
  {
    Home: { screen : followers,
            params : {user : 'Unn4m3DD'}
          }
  },
  {
    headerMode: 'none',
    navigationOptions: {
      headerVisible: false,
    },
    initialRoute: 'Home'
  })

  const App = createAppContainer(AppNavigator);

  export default App;

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'black',
  },
  username: {
    fontSize: 20,
    textAlign: 'center',
  },
  bio : {
    justifyContent:'flex-start',
  },
  image: {
    width: 100,
    height: 100,
  }
});
