import React from 'react';
import {StyleSheet, Text, View, TextInput, Image, TouchableWithoutFeedback} from 'react-native';

export default function search(screenProps) {
    return(
    <View style={styles.container}>
        <Text>Search for a user: </Text>
        <TextInput style={styles.search}/>
    </View>);

    async function checkUser(user) {
        const res = await fetch("https://api.github.com/users/" + user).then(res => res.json)
        if(res.message != "Not Found") screenProps.navigation.navigate('followers', {user: user})
    }
}

const styles = StyleSheet.create({
    search: {

    },
    container: {
        justifyContent: 'center',
        alignItems: 'center'
    }

})
